from typing import List


class Solution:
    memo = set()
    def maxAreaOfIsland(self, grid: List[List[int]]) -> int:
        res = []
        for r in range(len(grid)):
            for c in range(len(grid[0])):
                res.append(self.make_island(grid, r, c))
        return max(res)

    def make_island(self, grid: List[List[int]], r, c):
        if not (0 <= r < len(grid) and 0 <= c < len(grid[0]) and (r,c) not in self.memo and grid[r][c]):
            return 0
        self.memo.add((r, c))
        return (1 + self.make_island(grid, r+1, c) + self.make_island(grid, r-1, c) +
                self.make_island(grid, r, c-1) + self.make_island(grid, r, c+1))


